con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder
//Парсим запрос и ответ
def resp = new XmlHolder( messageExchange.responseContentAsXml )
def req = new XmlHolder( messageExchange.requestContentAsXml )
//Прописываем пространства имён
resp.namespaces["n"] = "http://www.parasoft.com/wsdl/calculator/"
resp.namespaces["soap"] = "http://schemas.xmlsoap.org/soap/envelope/"
req.namespaces["cal"] = "http://www.parasoft.com/wsdl/calculator/"
req.namespaces["soapenv"] = "http://schemas.xmlsoap.org/soap/envelope/"
//Считываем результат из ответа
Float result = new Float(resp.getNodeValue('//n:Result'))
//Считываем параметры из запроса
Integer x = new Integer(req.getNodeValue('//cal:x'))
Integer y = new Integer(req.getNodeValue('//cal:y'))
//Проверяем утверждение
assert result == x + y
